#!/usr/bin/env python# -*- coding: utf-8 -*-''' Ã  partir d'un fichier fasta, l'utilisateur passe un arg seq'''import argparse def create_parser():    parser = argparse.ArgumentParser()    parser.add_argument('-i','--inputfile',type=str,metavar="FASTA", required= True)    parser.add_argument('-seq','--sequences',type=str, required = True)    return parserdef main():    #declaration du parser 	parser = create_parser()	args = parser.parse_args() ; args = args.__dict__	print(args['inputfile'])if __name__ == "__main__":     main()